INSERT INTO `ai_chat_message` (`id`, `session_id`, `role`, `content`, `metadata`, `status`, `create_time`, `update_time`) VALUES (18, '1f5dc975d9074b179f2957ff277d33dc', 'AI', '好的小哀！我们来一步步安排吧～😊\n\n第一步：请问您后天（具体日期）几点入园呢？  \n第二步：您需要购买哪种门票呢？\n- 成人票 ¥110\n- 学生票 ¥60（需学生证）\n- 儿童票 ¥50\n\n第三步：请告诉我您的手机号，方便购票和接收信息哦～\n\n等你回复，我马上为您创建订单！🎟️🎋', NULL, 1, '2025-10-08 13:54:26', '2025-10-08 13:54:26');
