INSERT INTO `ai_chat_message` (`id`, `session_id`, `role`, `content`, `metadata`, `status`, `create_time`, `update_time`) VALUES (162, 'e4f710c51e7043e88939ee20800c99cf', 'AI', '好的！为了给您推荐最合适的游玩路线，我需要了解一些信息哦~ 😊\n\n🕐 **游玩时长**：您计划玩多久呢？（比如半天、一天、两天）  \n🚗 **出行方式**：您是自驾过来，还是乘坐公共交通呢？  \n👨‍👩‍👧‍👦 **同行人员**：有老人或小朋友一起吗？  \n🎯 **游玩偏好**：您更喜欢拍照打卡、轻松休闲，还是徒步探险呢？\n\n告诉我这些，我就能为您量身定制一条完美路线，还会推荐沿途的餐厅、卫生间、停车场等实用设施哦！💚', NULL, 1, '2025-11-05 10:49:19', '2025-11-05 10:49:19');
