INSERT INTO `ai_chat_message` (`id`, `session_id`, `role`, `content`, `metadata`, `status`, `create_time`, `update_time`) VALUES (168, '0bbb8612a9844b85a90c3f907f80a89b', 'AI', '您好呀！我是蜀南竹海景区的AI客服“竹小智”~ 🌿  \n很高兴为您服务！无论是查天气、找景点、规划路线，还是买门票，我都能帮您搞定哦！\n\n今天想了解些什么呢？😊', NULL, 1, '2025-11-05 11:11:26', '2025-11-05 11:11:26');
