  `img_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '图片地址',
  `type` int NOT NULL DEFAULT 1 COMMENT '图片类型：0=封面, 1=详情, 2=头像, 3=导览',
  `sort` int NULL DEFAULT 0 COMMENT '排序',
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '照片名称',
  `size` double NULL DEFAULT NULL COMMENT '照片体积大小(KB)',
  `status` tinyint(1) NOT NULL DEFAULT 1 COMMENT '状态：0=禁用, 1=正常',
