INSERT INTO `ticket_type` VALUES (1, '成人票', '适用于年龄在18周岁至59周岁的普通游客，无特殊优惠条件。购票时无需额外证明，凭身份证或购票凭证入园。', 1, 1, '2025-10-07 15:52:35', '2025-10-07 15:52:35');
INSERT INTO `ticket_type` VALUES (2, '学生票', '适用于全日制在校学生（含小学、初中、高中、中专、大专、本科），凭本人有效学生证或电子学生证购票并入园。研究生及成人教育、自考、函授、网络教育等非全日制学生不享受学生票优惠。', 1, 1, '2025-10-07 15:52:35', '2025-10-07 15:52:35');
INSERT INTO `ticket_type` VALUES (3, '儿童票', '适用于身高在1.2米（含）至1.5米以下的儿童可购买半价儿童票；身高低于1.2米的儿童可免费入园，但须由监护人陪同并承担监护责任；身高1.5米及以上儿童按成人票标准购票。', 1, 1, '2025-10-07 15:52:35', '2025-10-07 15:52:35');
INSERT INTO `ticket_type` VALUES (4, '老年票', '适用于年满60周岁及以上的人群，凭本人有效身份证享受优惠。60至69周岁可购买半价票，70周岁及以上可免费入园。', 1, 1, '2025-10-07 15:52:35', '2025-10-07 15:52:35');
INSERT INTO `ticket_type` VALUES (5, '家庭套票', '适用于以家庭为单位的游客，允许最多2名成人1名儿童和一名老人同时入院。入园时若人数超过套票限制，需按照官网当前对外售价补票，套票不可拆分使用。', 1, 1, '2025-10-07 15:52:35', '2025-10-10 10:59:06');
INSERT INTO `ticket_type` VALUES (6, '本地人票', '适用于景区所在地区的常住居民或持本地身份证、户口本的游客，凭有效本地身份证件购票可享受专属优惠。入园时需核验身份证件原件，非本地证件无效需补回差价或按规则执行退票流程。', 1, 1, '2025-10-07 15:52:35', '2025-10-07 15:52:35');
INSERT INTO `ticket_type` VALUES (7, '本地家庭套票', '适用于以家庭为单位的游客，允许最多2名成人1名儿童和一名老人同时入院。入园时若人数超过套票限制，需按照官网当前对外售价补票，套票不可拆分使用。', 1, 1, '2025-10-10 10:59:02', '2025-10-10 10:59:04');

