-- ----------------------------
-- Table structure for scenic_spot
-- ----------------------------
DROP TABLE IF EXISTS `scenic_spot`;
CREATE TABLE `scenic_spot`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '景点ID',
  `parent_id` bigint NULL DEFAULT 1 COMMENT '父级景区ID（NULL或1表示蜀南竹海景区，景点默认为1）',
  `spot_type` tinyint(1) NOT NULL DEFAULT 1 COMMENT '类型：0=景区（蜀南竹海）, 1=景点（景区内的景点，默认值）',
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '景点名称',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '景点描述',
  `longitude` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '经度',
  `latitude` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '纬度',
  `poi_id` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '高德地图POI ID',
  `address` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '文字地址',
  `model_3d_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '3D模型链接',
  `display_order` int NULL DEFAULT 0 COMMENT '展示顺序',
  `status` tinyint(1) NOT NULL DEFAULT 1 COMMENT '状态：0=禁用, 1=正常',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_parent_id`(`parent_id` ASC) USING BTREE,
  INDEX `idx_spot_type`(`spot_type` ASC) USING BTREE,
  INDEX `idx_name`(`name` ASC) USING BTREE,
  INDEX `idx_status`(`status` ASC) USING BTREE,
  INDEX `idx_display_order`(`display_order` ASC) USING BTREE,
  INDEX `idx_poi_id`(`poi_id` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 31 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '景点表（景区和景点两级结构）' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of scenic_spot
-- ----------------------------
INSERT INTO `scenic_spot` VALUES (1, NULL, 0, '蜀南竹海', '蜀南竹海位于四川省宜宾市长宁县、江安县一带，是国家AAAA级旅游景区，中国旅游目的地四十佳。蜀南竹海位于四川南部的宜宾市境内，幅员面积120平方公里，核心景区45平方公里，共有八大主景区，两大序景区134处景点。由27条峻岭，500多座峰峦组成，景区内共有竹子400余种，7万余亩，楠竹枝叠根连，葱绿俊秀，浩瀚壮观。蜀南竹海是世界上集中面积最大的天然竹林景区，独特的地理位置，造就了“云山竹海，天上人间”。是世界罕见、中国唯一的集竹景、山水、湖泊、瀑布、古庙于一体，同时兼有历史悠久的人文景观的竹文化、竹生态休闲度假旅游目的地。蜀南竹海的植被覆盖率达92.4%，景区内绿色怡人、空气清新，是一座天然的绿色大氧吧。更是川南地区著名的影视基地,《卧虎藏龙》、《大人物》、《大酒商》、《风云2》、《爱到春潮滚滚来》、《勇士》、浙江卫视《24小时》真人秀栏目等诸多影视作品和综艺节目都将蜀南竹海作为了外景拍摄地点。蜀南竹海，四季皆宜：春日，新笋遍地，生意盎然；盛夏，嫩竹泻翠，林荫蔽日，瀑飞泉涌，气候爽人；金秋，翠竹摇风，绿竹林中，红叶点点；隆冬，林寒涧肃，青枝白雪，相映成趣。', '104.982268', '28.460824', '110000', '四川省宜宾市长宁县蜀南竹海', NULL, 1, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (2, 1, 1, '忘忧谷', '忘忧谷位于蜀南竹海西南部，是丹霞地貌中典型的“Ⅴ”字型峡谷，以其幽静而著称，是蜀南竹海的标志性景点之一，谷内主要有“古佛洞”、“五叠屏”、“天生桥”、“石破天惊”、“九天涤尘”、“石上流”等景观。相传古代曾有贤者在此隐居，自云“乐以忘忧”，因得此名。谷内宁静幽深，竹林茂密，竹影丛丛，景中有景；小石径溯溪而上，斗折蛇行，阳光透过竹叶的间隙,在地上筛下点点金光，清润的山泉，一路跌宕，留下众多瀑布。谷中楠竹粗大而且密集，溪水哗哗，瀑布叠叠。林里荫黑如山雨欲来，而此时林外往往又红日当午。溪中的石头都长着青苔、汀兰、菖蒲，林风拂来，伏天也如凉秋。', '104.987374', '28.457401', '110000', '四川省宜宾市长宁县蜀南竹海忘忧谷', NULL, 2, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (3, 1, 1, '天宝寨', '天宝寨位于蜀南竹海东南部，紧邻仙寓洞。内有“隐秀壑”、“撑腰岩”、“半边楼遗址”、“黄伞石”、“锦绣田园”等景观。天宝寨原是一个天然岩腔，长约1500米，高20米左右，宽10米左右。上为悬空绝壁，下是千仞削壁，陡不可攀。这里景色壮丽，晴天，红色砂岩在阳光的照射下金碧辉煌。雨雾天气，云飘雾拂，景色奇丽。洞的四周翠竹围合，犹如天然屏障遮挡视线，人行其中，虽险不惊。古寨依天然石洞筑构，底部、顶部、里壁均系天然磐石赤长廊形半边洞，下临万仞陡岩,寨依洞成', '105.019448', '28.456695', '110000', '四川省宜宾市长宁县蜀南竹海天宝寨', NULL, 3, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (4, 1, 1, '七彩飞瀑', '七彩飞瀑又名落魂台，是蜀南竹海最壮观的瀑布群，处在石鼓山和石锣山之间的葫芦谷中。从蜀南竹海深处流出的水潦河，在回龙桥处跌向深谷，形成罕见的瀑布群。瀑布总高约200米，共有四级。其中最壮观的是高40米，宽4米的第三级。有人曾以“飞瀑凌空万古息”绘其形，“回声震吼惊天潮”状其声。飞流直下，水雾升腾。晴日的中午时分，阳光透过水雾，折映出七色光环，形成斑斓缤纷的彩虹，十分瑰丽、雄奇、壮观，七彩飞瀑由此得名。', '105.036577', '28.489872', '110000', '四川省宜宾市长宁县蜀南竹海七彩飞瀑', NULL, 4, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (5, 1, 1, '翡翠长廊', '翡翠长廊位于竹海竹林深处，是蜀南竹海标志性景观之一， 路面是由“色如渥丹、灿若明霞”的天然红色砂石铺成，长480米，宽6.5米，两旁密集的老竹新篁拱列，遮天蔽日。红色宛如地毯的道路与绿色屏风般的楠竹交相辉映，形成秀丽壮美的翠长廊，加之道路时起时伏，顶上两旁修竹争向内倾，几乎拱合，丹霞与翡翠交相辉映，从而成为蜀南竹海最具特色的标志性景观和拍照留影点。', '105.005864', '28.466682', '110000', '四川省宜宾市长宁县蜀南竹海翡翠长廊', NULL, 5, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (6, 1, 1, '天皇寺', '天皇寺是一座古刹，伫立在竹海深处的山峰之巅。登至寺顶，四下竹浪连绵、层峦起伏，云雾在竹海中浮沉，风过竹梢，涛声阵阵。置身其间，仿佛在万壑之间俯瞰苍翠竹海，无边无际，听风声细语，看竹影婆娑，令人心神涤荡。', '105.037468', '28.466506', '110000', '四川省宜宾市长宁县蜀南竹海天皇寺', NULL, 6, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (7, 1, 1, '龙吟寺', '龙吟寺是一座佛寺，也叫龙尾寺，坐落在海拔980米的九龙山上，为蜀南竹海第二高峰。登临绝顶，有“一览众山小”之感。这里翠浪起伏，浩渺连天，天风吹荡，竹涛声声，恍若龙吟，展现出一望无际的竹海奇观。', '105.002613', '28.484112', '110000', '四川省宜宾市长宁县蜀南竹海龙吟台', NULL, 7, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (8, 1, 1, '仙寓洞', '仙寓洞位于蜀南竹海南部，因自然景观和人文景观极佳被誉为“竹海明珠”。仙寓洞原是一个天然岩腔，长300余米，宽和高2米至15米。洞上是莽莽的楠竹林，洞下是蜀南竹海大峡谷。隔蜀南竹海大峡谷的是挂膀岩洞穴群。谷中，时或烟波浩渺，时或云飞雾聚，仙寓洞也就时隐时现于烟霭云雾之中。从洞中往外观，周围青绿翠蔓，碧浪无垠。若是天气晴朗，云消雾散，可鸟瞰十里田园景色。 仙寓洞又是一个依山靠岩建造的石窟寺庙。这里最早是一个道观，后来佛教兴盛。宋朝以后，相继修建了观音殿，大雄宝殿、小雄宝殿等佛教活动场所。清代又有道人黄化等在此开展道教活动，兴建了玉皇殿、老君殿等活动场所。仙寓洞，实际上是一个佛教和道教共同存在的宗教活动场所。', '105.017545', '28.458571', '110000', '四川省宜宾市长宁县蜀南竹海仙寓洞', NULL, 8, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (9, 1, 1, '仙女湖', '仙女湖是蜀南竹海的著名景点，位于仙寓洞景区山顶上，是蜀南竹海海拔最高的湖泊，与天宝寨、仙寓洞形成仙寓洞景区旅游环线。仙女湖面积三十多亩，湖水清澈碧绿。晨曦初升，夕阳西下，万道霞光之中，仙女湖犹如一个金色的湖盆，壮丽辉煌。晨雾烟雨之时，绿的山、翠的竹，又构成了一幅幅美丽的水彩画卷。', '105.027121', '28.460866', '110000', '四川省宜宾市长宁县蜀南竹海仙女湖', NULL, 9, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (10, 1, 1, '青龙湖', '青龙湖位于蜀南竹海东北部，为竹海最大的湖泊，面积1.51平方公里，位于高耸险峻的插旗山下，因湖水碧绿，湖身呈龙状，鳞爪分明，故名“青龙湖”。湖内鱼类众多，湖中有圆形小岛与青山交相辉映，如青龙献珠。湖面翠竹掩映，湖心的两座浑圆小岛，长满碧森森翠竹，湖光山色,一片碧绿', '105.057147', '28.497195', '110000', '四川省宜宾市长宁县蜀南竹海青龙湖', NULL, 10, 1, '2025-10-07 15:52:35', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (11, 1, 1, '海中海', '海中海是竹海山上一个海拔较低的槽谷，后筑坝形成湖泊，湖面面积约60亩，湖面空旷开阔，俨然茫茫竹海中的一片“海子”，故名海中海。海中海是获得奥斯卡最佳外语片等四项奖项和十项提名的经典武侠电影《卧虎藏龙》取景地，与青龙湖、仙女湖等天然湖泊在蜀南竹海内从山巅至山脚星罗棋布，构成了一幅山中有水、水中有山、山水相依、竹在其间的天然山水竹画卷。', '105.025075', '28.463714', '110000', '四川省宜宾市长宁县蜀南竹海海中海', NULL, 11, 1, '2025-10-09 21:42:05', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (12, 1, 1, '花溪十三桥', '花溪十三桥位于蜀南竹海北部，是一条主要以山居风光为特色的步行游览线。花溪原名潦水河，两岸翠竹林箐，农户错落其间，溪流婉曲，流水淙淙，清澈见底；溪边林间，野芳发而幽香，绿竹秀而繁阴。行走在这幽谷林间，有着辛弃疾《西江月》中“路转溪桥忽见”的意境。溪岸散布人家，有小桥连接两岸；大约1公里的溪流上，竹桥木桥而外，各式石桥，即达13座之多，其中还有8座系明清古桥。', '105.017624', '28.485746', '110000', '四川省宜宾市长宁县蜀南竹海花溪十三桥', NULL, 12, 1, '2025-10-09 21:47:15', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (13, 1, 1, '竹尖漫步', '竹尖漫步的主栈道全长约1.7公里，全程蜿蜒在竹林中，宛如游龙在竹间盘旋起舞、翻云吐雾，游客在这里可以近距离观赏楠竹，体验“竹尖漫步”。在这里远眺，不仅可以观赏波涛似的竹海，而且可看到远处的山水田园画卷。', '105.011564', '28.458005', '110000', '四川省宜宾市长宁县蜀南竹海竹尖漫步', NULL, 13, 1, '2025-10-09 21:53:01', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (14, 1, 1, '蜀南竹海博物馆', '蜀南竹海博物馆是全国最大的竹类专业博物馆， 占地面积6668米，建筑面积 3800 多平方米。博物馆集竹文物收藏保护、展览、学术研究和社会教育于一体，以“追溯千古民族文化，展示万载竹类精华”为宗旨。由自然馆、历史馆、民俗1馆2馆和竹韵馆四馆厅组成。', '104.982221', '28.461284', '110000', '四川省宜宾市长宁县蜀南竹海博物馆', NULL, 14, 1, '2025-10-09 21:55:02', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (15, 1, 1, '望龙坪', '望龙坪位于蜀南竹海北部边缘群山峻岭脊背上，在竹林的掩映之下，光与影就这样摇曳生姿显得九龙山更加的雄伟壮观、幽深秀丽，悬浮在竹海之上，每一寸风景都美得让人陶醉。', '104.985270', '28.482803', '110000', '四川省宜宾市长宁县蜀南竹海望龙坪', NULL, 15, 1, '2025-10-09 22:07:08', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (16, 1, 1, '青云长廊', '青云长廊位于蜀南竹海北部边缘的群山之巅，九龙山的山腰，是观赏最美丘陵、诗画田园的又一胜地。有诗描述：天阔云闲步长廊，清柔明朗润远山。站在岩顶远眺，百里沃野，尽奔眼底。临岩俯瞰、山麓、冈峦起伏似翻滚的波浪。近处，浅丘平坝如碧波涟漪；远处，丘海连绵，佛来山群峰逶迤。条条河带，层层梯田，缈缈炊烟，如诗如画。', '105.004488', '28.486798', '110000', '四川省宜宾市长宁县蜀南竹海青云长廊', NULL, 16, 1, '2025-10-09 22:09:33', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (17, 1, 1, '观海楼', '观海楼原名吊黄楼，与北宋著名文学家、书法家黄庭坚有关。北宋年间，黄庭坚悠游蜀南竹海，登临处，面对满目翠竹连声叹曰：“壮哉，壮哉，竹波万里！”这一叹，天地大美，绝响千年！后世在此建楼凭吊之。自宋朝以来，历朝历代多次修缮。今之观海楼，是在原址上恢复重建的。登楼远眺，那一山山，一岭岭，密集广布，浩瀚广阔的竹林之海，使人胸襟顿开，心旷神怡。临空俯瞰，座座峰峦，似翠色尽染；条条峻岭，如碧浪翻滚，千里烟波，尽涌眼底。', '105.005091', '28.468122', '110000', '四川省宜宾市长宁县蜀南竹海观海楼', NULL, 17, 1, '2025-10-09 22:10:50', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (18, 1, 1, '拜寿台', '拜寿台是2008年北京奥运会开幕式中国山水画卷“百龟拜寿”取景点，“四川 100个最美景观拍摄点”和蜀南竹海标志性景观之一。山下一座座浅丘，似不计其数的长寿神龟正在拜寿，故得名拜寿台，山下河流、水田蒸发的水汽在清晨形成云雾，云雾与丘陵、山峦相互映衬，宛如美丽山水画卷，“云山竹海、天上人间”在此充分体现。', '104.985586', '28.451742', '110000', '四川省宜宾市长宁县蜀南竹海拜寿台', NULL, 18, 1, '2025-10-09 22:13:53', '2025-10-13 11:42:08');
INSERT INTO `scenic_spot` VALUES (19, 1, 1, '永江休闲度假村', '永江休闲度假村位于蜀南竹海国家级旅游度假区南部，是度假区竹汤组团的重要组成部分，背靠国家级风景名胜区——蜀南竹海，是度假区南线中的自然村，交通便利。后河、红桥河在这里三江合流，汇成美丽的淯江河。“一山一水一田园，宜业宜居美如画”，永江村被誉为蜀南竹海“后花园”。', '104.940722', '28.447893', '080401', '宜宾市长宁县四川省宜宾市长宁县Y002', NULL, 19, 1, '2025-10-13 11:42:08', '2025-10-13 11:42:45');
INSERT INTO `scenic_spot` VALUES (20, 1, 1, '墨溪', '从蜀南竹海博物馆往左，可见一条水泥公路伸向莽莽的林中，迤逶而葱郁的竹林，护映着一条泛着青光的溪流，这便是墨溪。神奇峻秀的墨溪，是竹海风光最原始、最古朴的景区。', '104.998465', '28.470787', '110000', '宜宾市长宁县宜宾市长宁县万岭镇宜宾蜀南竹海风景名胜区', NULL, 20, 1, '2025-10-13 11:42:08', '2025-10-13 11:42:45');

-- ----------------------------
-- Table structure for scenic_ticket
-- ----------------------------
DROP TABLE IF EXISTS `scenic_ticket`;
CREATE TABLE `scenic_ticket`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `scenic_spot_id` bigint NOT NULL COMMENT '景点ID',
  `ticket_type_id` bigint NOT NULL COMMENT '票种ID',
  `price` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '价格',
  `sold_count` int NULL DEFAULT 0 COMMENT '门票售出数量',
  `status` tinyint(1) NOT NULL DEFAULT 1 COMMENT '状态：0=禁用, 1=正常',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_scenic_spot_id`(`scenic_spot_id` ASC) USING BTREE,
  INDEX `idx_ticket_type_id`(`ticket_type_id` ASC) USING BTREE,
  INDEX `idx_status`(`status` ASC) USING BTREE,
  INDEX `idx_price`(`price` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 8 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '景点票种关联表' ROW_FORMAT = Dynamic;

